mapper.update:
 update
		tbl_board set title=#{title}, content
		=#{content} where bno = #{bno}

mapper.select:
select * from (
select /*+INDEX_DESC(tbl_board pk_board) */ rownum rn, bno, title, content, writer, regdate, viewcnt from tbl_board where rownum <= #{page} * 10 and bno >0
) where rn> (#{page} - 1)*10



select * from
(
select /*+index_desc(tbl_board pk_board)*/ 
rownum rn, bno, title, writer, regdate, viecnt from tbl_board
where bno >  0



and rownum <= #{page} * #{perPage}
)
where rn>(#{page}-1)*#{perPage}
;
